<?phpclass MostrarJson {    public static function TraerTodosLasMascotas() {                if (!file_exists("archivos/mascotas.json"))        	return false;    	$archivo = fopen("archivos/mascotas.json", "r");    	$mascotas = "";    	while (!feof($archivo))    		$mascotas .= fgets($archivo);    	$mascotas = trim($mascotas, "\r\n");    	fclose($archivo);    	return $mascotas;    }}